import styles from "./styles.module.scss";
import classnames from "classnames";

interface Props {
  onSelect(file: File): void;
  disabled: boolean;
}

function BrowseFileButton({ onSelect, disabled }: Props) {
  const onClick = () => {
    if (disabled) {
      return;
    }
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "video/mp4";
    input.click();
    input.oninput = () => {
      if (input.files && input.files.length > 0) {
        onSelect(input.files[0]);
      }
    };
  };
  return (
    <div
      onClick={onClick}
      className={classnames(
        styles.container,
        "d-flex flex-col ai-center cursor-pointer user-select-none",
        { "opacity-60": disabled },
      )}
    >
      <svg
        width="41"
        height="42"
        viewBox="0 0 41 42"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <rect y="0.5" width="41" height="41" rx="8" fill="url(#pattern0)" />
        <rect
          x="0.5"
          y="1"
          width="40"
          height="40"
          rx="7.5"
          stroke="black"
          strokeOpacity="0.3"
        />
        <defs>
          <pattern
            id="pattern0"
            patternContentUnits="objectBoundingBox"
            width="1"
            height="1"
          >
            <use
              href="#image0_4729_26363"
              transform="translate(0 -0.389583) scale(0.00416667)"
            />
          </pattern>
          <image
            id="image0_4729_26363"
            width="240"
            height="427"
            href="data:image/png;base64,"
          />
        </defs>
      </svg>
      <span className="tg-label-sm color-black mt-1">Browse</span>
    </div>
  );
}

export { BrowseFileButton };
